import Input from './components/Input';
import Button from './components/Button';

import { Container, Content, Row } from './styles';
import { useState } from 'react';


const App = () => {
  const [ currentNumber, setCurrentNumber ] = useState('0');
  const [firstNumber, setFirstNumber] = useState ('0');                {/* function first number */}
  const [operation, setOperation] = useState ('');



  {/* Function to delete */}
  const handleOnClear = () => {
    setCurrentNumber('0');
    setFirstNumber('0');
    setOperation('');
  }

  const handleAddNumber = (number) => {
    setCurrentNumber(prev =>`${prev === '0' ? '':prev}${number}`);
  }

  {/* conditional to perform sum */}
  const handleSumNumbers = () => {
    if (firstNumber === '0'){
        setFirstNumber(String(currentNumber));
        setCurrentNumber('0')
        setOperation('+')
    } else {
        const sum = Number(firstNumber) + Number(currentNumber);
        setCurrentNumber(String(sum))
        setOperation('')
    }
  }

  {/* conditional to perform subtraction*/}
  const handleSubNumbers = () => {
    if (firstNumber === '0'){
        setFirstNumber(String(currentNumber));
        setCurrentNumber('0')
        setOperation('-')
    } else {
        const sum = Number(firstNumber) - Number(currentNumber);
        setCurrentNumber(String(sum))
        setOperation('')
    }
  }

  {/* conditional to perform division*/}
  const handleDivNumbers = () => {
    if (firstNumber === '0'){
        setFirstNumber(String(currentNumber));
        setCurrentNumber('0')
        setOperation('/')
    } else {
        const sum = Number(firstNumber) / Number(currentNumber);
        setCurrentNumber(String(sum))
        setOperation('')
    }
  }

  {/* conditional to perform the square root*/}
  const handleRootNumbers = () => {
    if (currentNumber !== '') {
      const root = Math.sqrt(Number(currentNumber));

      setCurrentNumber(String(root));
      setOperation('');
    }
  };

  {/* conditional to perform multiplication*/}
  const handleMulNumbers = () => {
    if (firstNumber === '0'){
        setFirstNumber(String(currentNumber));
        setCurrentNumber('0')
        setOperation('*')
    } else {
        const sum = Number(firstNumber) * Number(currentNumber);
        setCurrentNumber(String(sum))
        setOperation('')
    }
  }

  {/* conditional for the realization of "equal" */}
  const handleEquals = () => {
    if (firstNumber !== '0' && operation !== '' && currentNumber !== '0'){
        switch(operation){
          case '+':
            handleSumNumbers();
            break;
          case '-':
            handleSubNumbers();
            break;
          case '/':
            handleDivNumbers();
            break;
          case '√':
            handleRootNumbers();
            break;
          case '*':
            handleMulNumbers();
            break;
          default: 
            break;
        }
    }
  }

  return (
    <Container>
      <Content>
        <Input value = {currentNumber}/>

        {/* variables for calculator formatting ( Button )*/}
        <Row>
          <Button label="*" onClick={handleMulNumbers}/>
          <Button label="/" onClick={handleDivNumbers}/>
          <Button label="√" onClick={handleRootNumbers}/>
          <Button label="C" onClick={handleOnClear}/>
        </Row>
        <Row>
          <Button label="7" onClick={() => handleAddNumber('7')}/>
          <Button label="8" onClick={() => handleAddNumber('8')}/>
          <Button label="9" onClick={() => handleAddNumber('9')}/>
          <Button label="-" onClick={handleSubNumbers}/>
        </Row>
        <Row>
          <Button label="4" onClick={() => handleAddNumber('4')}/>
          <Button label="5" onClick={() => handleAddNumber('5')}/>
          <Button label="6" onClick={() => handleAddNumber('6')}/>
          <Button label="+" onClick={handleSumNumbers}/>
        </Row>
        <Row>
          <Button label="1" onClick={() => handleAddNumber('1')}/>
          <Button label="2" onClick={() => handleAddNumber('2')}/>
          <Button label="3" onClick={() => handleAddNumber('3')}/>
          <Button label="=" onClick={handleEquals}/>
        </Row>
        <Row>
          <Button label="0" onClick={() => handleAddNumber('0')}/>
        </Row>

      </Content>
    </Container>);
}
export default App;
